mapper.insert:
INSERT INTO USER_INFO(
    USER_SEQ, USER_ID, USER_PW, USER_NAME, USER_AGE,  USER_DT

)VALUES (
            #{user_seq}, #{user_id}, #{user_pw},#{user_name}, #{user_age},SYSDATE()
        )

mapper.select:
SELECT (CASE WHEN COUNT(1) THEN 'Y' ELSE 'N' END) AS EXISTS_YN
FROM USER_INFO
WHERE USER_ID = #{user_id}

SELECT USER_ID
FROM USER_INFO
WHERE USER_ID = #{user_id}
  AND USER_PW = #{user_pw}
