INSERT INTO USER_INFO(
    USER_SEQ,USER_EMAIL, USER_ID, USER_PW, USER_NAME, USER_AGE, USER_GENDER, USER_DT

)VALUES (
            #{user_seq},#{user_email}, #{user_di}, #{user_pw},#{user_name}, #{user_age}, #{user_gender},#{user_gender}
        )

SELECT DECOFE(COUNT(1) 0, 'N', 'Y') AS EXISTS_YN
FROM USER_INFO
WHERE EUSER_EMAI = #{user_email}

SELECT USER_ID
FROM USER_INFO
WHERE USER_ID = #{user_id}
  AND USER_PW = #{user_pw}
